<!--<h2>Products</h2>-->

<div *ngFor="let product of products">
  <div *ngIf="category && category.name == product.category" class="product-main">
    <div class="product-image">
      <img [src]="product.image" [alt]="product.name">
    </div>
      <h3>
      <a
        [title]="product.name + ' details'"
        [routerLink]="['/products', product.id]">
        {{ product.name }}
      </a>
    </h3>
    <h3>Rating: {{product.rating}}</h3>
    <h3>Price: <strong>{{product.price | currency:'₸'}}</strong></h3>
    <p *ngIf="product.description">Description: {{ product.description }}</p>
    <button type="button" (click)="share(product.url)">Share</button>
    <div class="kudos"
         data-amount="0"
         data-url="domain.tld/my-awesome-article"></div>
    <app-product-alerts [product]="product"> </app-product-alerts>
  </div>
</div>
